INSERT INTO `wp_posts` VALUES("51", "1", "2019-05-20 06:39:30", "2019-05-20 06:39:30", "<!-- wp:html -->\n[row]\n\n[col span=\"3\" span__sm=\"12\" align=\"center\" visibility=\"hide-for-small\"]\n\n[message_box bg_color=\"rgb(130, 74, 26)\" padding=\"0\"]\n\n[row_inner style=\"collapse\" v_align=\"middle\" h_align=\"center\"]\n\n[col_inner span=\"9\" span__sm=\"12\"]\n\n<p style=\"text-align: center;\"><strong>DANH MC SN PHM</strong></p>\n\n[/col_inner]\n\n[/row_inner]\n\n[/message_box]\n[ux_sidebar id=\"menu-left\"]\n\n[message_box bg_color=\"rgb(130, 74, 26)\" padding=\"0\"]\n\n[row_inner style=\"collapse\" v_align=\"middle\" h_align=\"center\"]\n\n[col_inner span=\"9\" span__sm=\"12\"]\n\n<p style=\"text-align: center;\"><strong>SN PHM BN CHY</strong></p>\n\n[/col_inner]\n\n[/row_inner]\n\n[/message_box]\n[ux_products_list products=\"4\" orderby=\"sales\" show=\"onsale\"]\n\n\n[/col]\n[col span=\"9\" span__sm=\"12\"]\n\n<p><span style=\"color: #282828; font-size: 80%;\"><b>Cng ty TNHH SX TM DV Nng sn Thc Phm Ht Vit (Vietnuts)</b>chuyn sn xut  ch bin<strong>ht iu, u phng, u nnh</strong>v cc sn phm n lin t cc ht n c (Edible nuts) c ngun gc thc vt</span></p>\n<div class=\"headline\">\n<h3><span style=\"color: #282828; font-size: 80%;\">Phng chm hot ng</span></h3>\n</div>\n<ul class=\"unstyled\">\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>Vi khch hng: Vietnuts l cng ty cung cp cc sn phm<strong>ht iu</strong>n lin a dng cht lng cao, m bo an ton v sinh thc phm.</span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>Vi tp th: Vietnuts l cng ty c mi trng lm vic thn thin, lun v tp th chung, v tt c v li ch ca khch hng</span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>S pht trin ca Vietnuts: s l mt tp on sn xut ln mnh, khng nhng  th trng Vit Nam, m cn vn xa n cc th trng trn th gii, tr thnh<strong><em>cng ty xut khu ht iu</em></strong>ton cu</span></li>\n</ul>\n<p><span style=\"color: #282828; font-size: 80%;\">Vi phng chm hat ng trn, Vietnuts mong mun tr thnh i tc quan trng i vi cc n v lin doanh, cng nh l mt tp on uy tn i vi khch hng. Vietnuts cng vi i ng nhn vin dy dn kinh nghim, phong cch lm vic chuyn nghip s lun phn u  t c mc tiu  ca mnh.</span></p>\n<div class=\"headline\">\n<h3><span style=\"color: #282828; font-size: 80%;\">nh hng chin lc</span></h3>\n</div>\n<ul class=\"unstyled\">\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>Vietnuts tr thnh<strong>cng ty sn xut ht iu</strong>hng u ti Vit Nam trong lnh vc ch bin<strong>ht iu</strong>n lin cht lng cao.</span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i><strong>Cng ty ht iu</strong>Vietnuts s cung cp gi thnh sn phm cnh tranh nht  ngi dn Vit Nam c th s dng<strong>ht iu</strong>mt sn phm b dng ton din.</span></li>\n</ul>\n<div class=\"headline\">\n<h3><span style=\"color: #282828; font-size: 80%;\">Cam kt</span></h3>\n</div>\n<ul class=\"unstyled\">\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>Cam kt cung cp cho khch hng sn phm cht lng cao, m bo an ton v sinh thc phm theo phong cch chuyn nghip.</span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>Tn trng cc nguyn tc o c ngh nghip v o c kinh doanh.</span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>o to v xy dng i ng nhn vin c chuyn mn v nng lc gii  nng cao cht lng sn phm ng thi phc v khch hng trong tin trnh pht trin v hi nhp ton cu. To mi trng lm vic chuyn nghip v thn thin.</span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>To s khc bit trong cc sn phm bng vic lun ci tin cht lng, gi thnh cnh tranh.</span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>ng dng cng ngh tin tin trong mi hot ng sn xut v kinh doanh.</span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>Phn u em li li ch cao nht cho khch hng, nhn vin v cng ng x hi</span></li>\n</ul>\n<div class=\"headline\">\n<h3><span style=\"color: #282828; font-size: 80%;\">Thnh vin</span></h3>\n</div>\n<ul class=\"unstyled\">\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>Thnh vin chnh thc<strong>HIP HIIU VIT NAM (VINACAS)</strong></span></li>\n<li><span style=\"color: #282828; font-size: 80%;\"><i class=\"icon-ok color-green\"></i>Thnh vin chnh thc<strong>HIP HI TRANG TRIVDOANH NGHIPNNG NGHIPVIT NAM (VFAEA)</strong></span></li>\n</ul>\n\n[/col]\n\n[/row]\n\n<!-- /wp:html -->", "Gii Thiu", "", "publish", "closed", "closed", "", "gioi-thieu", "", "", "2019-05-21 02:13:11", "2019-05-21 02:13:11", "", "0", "http://mualatang.com/?page_id=51", "0", "page", "", "0");
